import DetailNavBar from './childComps/DetailNavBar';
import DetailSwiper from './childComps/DetailSwiper';
import DetailBaseInfo from './childComps/DetailBaseInfo';
import DetailShopInfo from './childComps/DetailShopInfo';
import DetailGoodsInfo from './childComps/DetailGoodsInfo';
import DetailParamInfo from './childComps/DetailParamInfo';
import Scroll from 'components/common/scroll/Scroll';
import { getDetail, Goods, Shop, GoodsParam } from "network/detail";
export default {
  name: "Detail",
  components: {
    DetailNavBar: DetailNavBar,
    DetailSwiper: DetailSwiper,
    DetailBaseInfo: DetailBaseInfo,
    DetailShopInfo: DetailShopInfo,
    DetailGoodsInfo: DetailGoodsInfo,
    DetailParamInfo: DetailParamInfo,
    Scroll: Scroll
  },
  data: function data() {
    return {
      iid: null,
      topImages: [],
      goods: {},
      shop: {},
      detailInfo: {},
      paramInfo: {}
    };
  },
  created: function created() {
    var _this = this;

    // 1.保存传入的iid
    this.iid = this.$route.params.iid; // 2.根据iid请求详情数据

    getDetail(this.iid).then(function (res) {
      // 1.获取顶部的图片轮播数据
      console.log(res);
      var data = res.result;
      _this.topImages = data.itemInfo.topImages; // 2.获取商品信息

      _this.goods = new Goods(data.itemInfo, data.columns, data.shopInfo.services); // 3.创建店铺信息的对象

      _this.shop = new Shop(data.shopInfo); // 4.保存商品的详情数据

      _this.detailInfo = data.detailInfo; // 5.获取参数的信息

      _this.paramInfo = new GoodsParam(data.itemParams.info, data.itemParams.rule);
    });
  },
  methods: {
    imageLoad: function imageLoad() {
      this.$refs.scroll.refresh();
    }
  }
};
